$(document).ready(function(){$(".navbar a, footer a[href='#myPage']").on("click",function(o){o.preventDefault();var n=this.hash;$("html, body").animate({scrollTop:$(n).offset().top},900,function(){window.location.hash=n})}),$(window).scroll(function(){$(".slideanim").each(function(){$(this).offset().top<$(window).scrollTop()+600&&$(this).addClass("slide")})})});